function findUniqueCharacter(characters) {
    const charCount = {};
    for (const char of characters) {
        charCount[char] = (charCount[char] || 0) + 1;
    }
    for (const char of characters) {
        if (charCount[char] === 1) {
            return char;
        }
    }
    return null;
}
