public static char findUniqueCharacter(char[] characters) {
    for (char c : characters) {
        if (countOccurrences(characters, c) == 1) {
            return c;
        }
    }
    return '\0';
}

private static int countOccurrences(char[] characters, char c) {
    int count = 0;
    for (char ch : characters) {
        if (ch == c) {
            count++;
        }
    }
    return count;
}

