function findUniqueCharacter(characters) {
    const uniqueCharacters = new Set(characters);
    for (let char of uniqueCharacters) {
        if (characters.filter(c => c === char).length === 1) {
            return char;
        }
    }
}

// 테스트
console.log(findUniqueCharacter(['a', 'b', 'c', 'a', 'd']));  // 'b'
