const findUniqueCharacter = (characters) => {
  const uniqueChars = new Set();
  for (const char of characters) {
    const sortedChar = char.split("").sort().join("");
    if (!uniqueChars.has(sortedChar)) {
      uniqueChars.add(sortedChar);
    }
  }
  for (const char of uniqueChars) {
    if (characters.filter(c => c === char).length === 1) {
      return char;
    }
  }
};

