def find_unique_character(characters):
    # Create a dictionary to store the count of each character
    char_count = {}
    for char in characters:
        if char in char_count:
            char_count[char] += 1
        else:
            char_count[char] = 1

    # Find the first unique character
    for char in characters:
        if char_count[char] == 1:
            return char

